sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	// Instantiate the GetEmailAddress Script Include 
	var getHasCar = new GlideAjax('GetHasCarValue');
	// Specify the getHasCarValue method
	getHasCar.addParam('sysparm_name','getHasCarValue');
	// Pass the parent name
	getHasCar.addParam('sysparm_parentName', g_form.getValue('parent'));
	// Send the request to the server
	getHasCar.getXML(populateHasCarField);


	// When the response is back from the server
	function populateHasCarField(response){
		var hasCarValueFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_has_car_4');
		g_form.setValue('u_has_car_4',hasCarValueFromScriptInclude);
	}

}
